import 'package:flutter/material.dart';import '../../constants/colors.dart';import '../../constants/font.dart';class AccountScreen extends StatelessWidget {   final List userAchieves = [    {      "icon": Icons.videocam,      "title": "10 Hours"    },    {      "icon": Icons.call_to_action_sharp,      "title": "10 Courses"    },    {      "icon": Icons.star,      "title": "4.9"    },  ];   final List settings = [    {      "icon": Icons.settings,      "name": "Settings",    },    {      "icon": Icons.credit_card,      "name": "Payment Details",    },    {      "icon": Icons.military_tech_outlined,      "name": "Achievement",    },    {      "icon": Icons.heart_broken,      "name": "Love",    },    {      "icon": Icons.remember_me,      "name": "Typing",    },  ];  @override  Widget build(BuildContext context) {    Size size = MediaQuery.of(context).size;    return Scaffold(      backgroundColor: kWhite,      appBar: getAppBar(),      body: getBody(size),    );  }  AppBar getAppBar() {    return AppBar(      backgroundColor: kWhite,      elevation: 1,      title: Text('Profile', style: TextStyle(fontWeight: kW8, fontSize: font_18, color: kBlack),),      actions: [        IconButton(            onPressed: () {},            icon: Icon(Icons.more_vert, color: kBlack,)        )      ],    );  }  Widget getBody(size) {    return SingleChildScrollView(      child: Column(        children: [          SizedBox(height: 60,),          Center(            child: Column(              children: [                Stack(                  clipBehavior: Clip.none,                  children: [                    Container(                      height: 95,                      width: 95,                      decoration: BoxDecoration(                        borderRadius: BorderRadius.circular(15),                        image: DecorationImage(                            image: NetworkImage("https://lh3.googleusercontent.com/a-/AOh14Ggibf7DAm7F_DwTM4czKKWEI8a7T--IvdzFanUO=s600-k-no-rp-mo"),                            fit: BoxFit.cover                        ),                      ),                    ),                    Positioned(                        bottom: -4,                        right: -4,                        child: Container(                          height: 26,                          width: 26,                          decoration: BoxDecoration(                              shape: BoxShape.circle,                              color: kBlack                          ),                          child: Icon(Icons.edit_outlined, color: kWhite, size: 16,),                        )                    ),                  ],                ),                SizedBox(height: 10,),                Text(                  "Pujan Bohora",                  style: TextStyle(                      fontSize: 16,                      fontWeight: FontWeight.bold                  ),                ),                SizedBox(height: 3,),                Text(                  "Learner",                  style: TextStyle(                      fontSize: 12,                      color: gray_600                  ),                ),              ],            ),          ),          SizedBox(height: 20,),          Padding(            padding: const EdgeInsets.symmetric(horizontal: 15),            child: Wrap(              spacing: 15,              children: List.generate(userAchieves.length, (index) {                return Container(                  height: 60,                  width: (size.width - 61) / 3,                  decoration:   BoxDecoration(                    borderRadius: BorderRadius.circular(15),                    color: kBlack,                    boxShadow: [                      BoxShadow(                        color: kBlack.withOpacity(0.5),                        blurRadius: 5,                        offset: Offset(0, 5),                      )                    ],                  ),                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      Icon(userAchieves[index]['icon'], color: kWhite, size: 25,),                      Text(                        userAchieves[index]['title'],                        style: TextStyle(                            fontSize: 12,                            fontWeight: FontWeight.w500,                            color: kWhite                        ),                      )                    ],                  ),                );              }),            ),          ),          SizedBox(height: 40,),          Column(            children: List.generate(settings.length, (index) {              return ListTile(                onTap: () {},                leading: Container(                  height: 45,                  width: 45,                  decoration: BoxDecoration(                      borderRadius: BorderRadius.circular(10),                      color: kBlack                  ),                  child: Icon(settings[index]['icon'], color: kWhite,),                ),                title: Text(                  settings[index]['name'],                  style: TextStyle(                      fontSize: 16,                      fontWeight: FontWeight.bold                  ),                ),              );            }),          ),        ],      ),    );  }}