import 'package:elearning/constants/font.dart';import 'package:flutter/material.dart';import '../../constants/colors.dart';class CourseScreen extends StatefulWidget {  const CourseScreen({Key? key}) : super(key: key);  @override  _CourseScreenState createState() => _CourseScreenState();}class _CourseScreenState extends State<CourseScreen>    with SingleTickerProviderStateMixin {  final int _listItemCount = 300;  final int _tabCount = 8;  TabController? _tabController;  @override  void initState() {    _tabController = TabController(length: _tabCount, vsync: this);    super.initState();  }  @override  Widget build(BuildContext context) {    return DefaultTabController(      length: 2,      child: NestedScrollView(        physics: NeverScrollableScrollPhysics(),        headerSliverBuilder: (context, innerScrolled) => <Widget>[          SliverOverlapAbsorber(            handle: NestedScrollView.sliverOverlapAbsorberHandleFor(context),            sliver: SliverSafeArea(top: false, sliver: courseHeader()),          )        ],        body: TabBarView(          children: [            SingleChildScrollView(              child: Column(                crossAxisAlignment: CrossAxisAlignment.start,                mainAxisAlignment: MainAxisAlignment.center,                children: [                  Container(child: Text("Lesson incomplete"),)                ],              ),            ),            SingleChildScrollView(              child: Container(child: Text("Lesson completed"),)            ),          ],        ),      ),    );  }}SliverAppBar courseHeader() {  return SliverAppBar(    automaticallyImplyLeading: false,    backgroundColor: Colors.white,    pinned: true,    floating: true,    snap: false,    expandedHeight: 110,    collapsedHeight: 110,    flexibleSpace: Container(        decoration: BoxDecoration(            border: Border(                bottom: BorderSide(                  color: Colors.transparent,                ))),        child: FlexibleSpaceBar(            titlePadding: EdgeInsets.zero,            centerTitle: true,            title: Center(              child: Column(                crossAxisAlignment: CrossAxisAlignment.center,                mainAxisAlignment: MainAxisAlignment.end,                children: [                  Container(                    width: double.infinity,                    padding: EdgeInsets.symmetric(horizontal: 10, vertical: 16),                    decoration: BoxDecoration(                        border: Border(                            bottom: BorderSide(                              color: gray_500,                              width: 1,                            ))),                    child: Center(                      child: Text(                        "Lessons",                        style: TextStyle(fontWeight: kW8, fontSize: font_18, color: kBlack),                      ),                    ),                  ),                  Container(                    height: 42,                    padding: EdgeInsets.zero,                    margin: EdgeInsets.zero,                    child: TabBar(                      indicator: UnderlineTabIndicator(                        borderSide: BorderSide(width: 3.0 ),                      ),                      padding: EdgeInsets.symmetric(vertical: 0),                      tabs: [                        Tab(                            child: Center(                                child: Row(                                  mainAxisAlignment: MainAxisAlignment.center,                                  children: [                                    Icon(Icons.play_circle_outline, size: 20,),                                    // SvgPicture.asset(                                    //   refer_and_earn,                                    //   height: 20,                                    //   width: 20,                                    // ),                                    SizedBox(                                      width: 10,                                    ),                                    Text("On going", style: TextStyle(color: kBlack),),                                  ],                                ))),                        Tab(                            child: Center(                                child: Row(                                  mainAxisAlignment: MainAxisAlignment.center,                                  children: [                                    Icon(Icons.incomplete_circle,size: 20,),                                    // SvgPicture.asset(                                    //   share_and_earn,                                    //   height: 20,                                    //   width: 20,                                    // ),                                    SizedBox(                                      width: 10,                                    ),                                    Text("Completed", style: TextStyle(color: kBlack),),                                  ],                                ))),                      ],                    ),                  ),                ],              ),            ))),  );}