import 'package:carousel_slider/carousel_slider.dart';import 'package:flutter/material.dart';import '../../../constants/colors.dart';class CategorySliderScreen extends StatefulWidget {  const CategorySliderScreen({Key? key}) : super(key: key);  @override  State<CategorySliderScreen> createState() => _CategorySliderScreenState();}class _CategorySliderScreenState extends State<CategorySliderScreen> {  int activeCarouselIndex = 0;  int selectedCategoryIndex = 0;  List banners = [    "https://i.pinimg.com/564x/38/0c/39/380c39c81ba934b29209eaa9d9e97575.jpg",    "https://i.pinimg.com/564x/38/0c/39/380c39c81ba934b29209eaa9d9e97575.jpg",    "https://i.pinimg.com/564x/38/0c/39/380c39c81ba934b29209eaa9d9e97575.jpg",    ];  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 15),      child: Column(        children: [          CarouselSlider(            options: CarouselOptions(                aspectRatio: 16 / 9,                viewportFraction: 1.0,                autoPlayCurve: Curves.fastLinearToSlowEaseIn,                autoPlayAnimationDuration: Duration(seconds: 2),                onPageChanged: (index, reason) {                  setState(() {                    activeCarouselIndex = index;                  });                }),            items: List.generate(banners.length, (index) {              return Container(                decoration: BoxDecoration(                  borderRadius: BorderRadius.circular(15),                  image: DecorationImage(                      image: NetworkImage("https://i.pinimg.com/564x/38/0c/39/380c39c81ba934b29209eaa9d9e97575.jpg"),                      fit: BoxFit.cover),                ),              );            }),          ),          SizedBox(            height: 10,          ),          Row(            mainAxisAlignment: MainAxisAlignment.center,            children: List.generate(banners.length, (index) {              return activeCarouselIndex == index                  ? Padding(                padding: const EdgeInsets.only(right: 8),                child: Container(                  height: 10,                  width: 30,                  decoration: BoxDecoration(                      borderRadius: BorderRadius.circular(5),                      color: blue_1),                ),              )                  : Padding(                padding: const EdgeInsets.only(right: 8),                child: Container(                  height: 10,                  width: 10,                  decoration: BoxDecoration(                      shape: BoxShape.circle, color: gray_100),                ),              );            }),          ),        ],      ),    );  }}