import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';import '../../../constants/app_image.dart';import '../../../constants/colors.dart';class SearchField extends StatelessWidget {  const SearchField({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Flexible(        child: Container(          height: 45,          decoration: BoxDecoration(            color: gray_100,            borderRadius: BorderRadius.circular(4),          ),          child: InkWell(            child: IgnorePointer(              child: TextField(                readOnly: true,                enabled: false,                textAlignVertical: TextAlignVertical.center,                decoration: InputDecoration(                  contentPadding: EdgeInsets.symmetric(vertical: 10),                  alignLabelWithHint: true,                  border: InputBorder.none,                  focusedBorder: InputBorder.none,                  enabledBorder: InputBorder.none,                  hintText: "What are you searching for?",                  hintStyle: TextStyle(color: gray_600),                  prefixIcon: Padding(                    padding: const EdgeInsetsDirectional.only(                        top: 10, bottom: 10, start: 5),                    child: SvgPicture.asset(                      search,                      color: gray_600,                    ),                  ),                ),              ),            ),          ),        ));  }}