import 'package:flutter/material.dart';class CommonViewModel extends ChangeNotifier{  bool _showLoading = false;  bool get showLoading => _showLoading;  bool _connected = false;  bool get connnected => _connected;  int _navigationIndex = 0;  int get navigationIndex => _navigationIndex;  PageController _pagecontroller = PageController();  PageController get pagecontroller => _pagecontroller;  setNavigationIndex(int index) {    _navigationIndex = index;    notifyListeners();  }  setLoading(bool state) {    _showLoading = state;    notifyListeners();  }  setInitial(int index) {    _pagecontroller = PageController(initialPage: index);    setNavigationIndex(index);    notifyListeners();  }  itemTapped(int index) {    setNavigationIndex(index);    _pagecontroller.jumpToPage(index);    notifyListeners();  }}