import 'package:flutter/material.dart';class InputContainer extends StatelessWidget {  String? labelText;  TextEditingController? controller;  bool? isPasswordTextField;  Icon? icons;  bool? enabled;  String? compulsory;  final String? Function(String?)? validator;  TextInputType? abc;  // InputContainer({Key? key}) : super(key: key);  InputContainer(      {Key? key,        this.compulsory,        this.validator,        this.abc,        this.labelText,        this.controller,        this.isPasswordTextField,        this.icons,        this.enabled})      : super(key: key);  @override  Widget build(BuildContext context) {    final node = FocusScope.of(context);    return Padding(      padding: const EdgeInsets.only(bottom: 15.0),      child: TextFormField(        validator: validator,        enabled: this.enabled,        controller: controller,        obscureText: isPasswordTextField!,        keyboardType: abc,        decoration: InputDecoration(            prefixIcon: icons,            labelStyle: TextStyle(color: Colors.black),            filled: true,            enabledBorder: const OutlineInputBorder(                borderSide: BorderSide(color: Colors.grey)),            focusedBorder: const OutlineInputBorder(                borderSide: BorderSide(color: Colors.green)),            // border:            //     OutlineInputBorder(borderRadius: BorderRadius.circular(100)),            floatingLabelBehavior: FloatingLabelBehavior.always,            label: Row(              children: [                Text(labelText!),                Text(                  compulsory!,                  style: TextStyle(color: Colors.red, fontSize: 25),                ),              ],            )),      ),    );  }}